function handleTab(t,e){const a=document.querySelectorAll(".tab__content"),c=document.querySelectorAll(".tab__links-item");for(i=0;i<a.length;i++)a[i].style.display="none";for(i=0;i<c.length;i++)c[i].className=c[i].className.replace(" tab__links-item--active","");document.getElementById(e).style.display="block",t.currentTarget.className+=" tab__links-item--active"}const tabContent=document.querySelector(".tab__content");if(tabContent){document.querySelectorAll(".tab-filter__button").forEach((t=>{t.addEventListener("click",(function(e){t.classList.contains("tab-filter__button--active")?t.classList.remove("tab-filter__button--active"):t.classList.add("tab-filter__button--active")}))}))}const articlePage=document.querySelector(".article-page");if(articlePage){document.querySelectorAll(".article__tag").forEach((t=>{t.addEventListener("click",(function(e){t.classList.contains("article__tag--chosen")?t.classList.remove("article__tag--chosen"):t.classList.add("article__tag--chosen")}))}));document.querySelectorAll(".article__data-info-item--action").forEach((t=>{t.addEventListener("click",(function(e){t.classList.contains("article__data-info-item--chosen")?t.classList.remove("article__data-info-item--chosen"):t.classList.add("article__data-info-item--chosen")}))}))}